NexT.plugins.others.math=function(){const e=NexT.CONFIG.page.math.render;if(e==="mathjax"){const e=NexT.utils.getCDNResource(NexT.CONFIG.page.math.js);NexT.utils.getScript(e,function(){window.MathJax={tex:{inlineMath:[["$","$"]]}}})}if(e==="katex"){const e=NexT.utils.getCDNResource(NexT.CONFIG.page.math.css);NexT.utils.getStyle(e);const t=NexT.CONFIG.page.math.js;t.forEach(e=>{const t=NexT.utils.getScript(NexT.utils.getCDNResource(e));e.name==="auto-render"&&t.then(function(){renderMathInElement(document.body,{delimiters:[{left:"$$",right:"$$",display:!0},{left:"$",right:"$",display:!1},{left:"\\(",right:"\\)",display:!1},{left:"\\[",right:"\\]",display:!0}],throwOnError:!1})})})}}